import React, { Component } from "react";
import "../styles/Footer.css";
import Contact from "../components/Contact";
import email from "../icons/email.svg";
import github from "../icons/github.png";
import phone from "../icons/phone.svg";
import linkedin from "../icons/linkedin.svg";

export default class Footer extends Component {
  render() {
    return (
      <div className="footer_container">
        <h1>Let's get in touch.</h1>
        <div className="final_comments">
          <p>
            As much as I love one-on-one time with my MacBook, the most valuable
            aspect of software development for me is working with others. Let's
            work together to make a beautiful website tailored to the needs of
            your business.
          </p>
        </div>
        {/* <h2>Connect with me through any of the means available below</h2> */}
        <div className="contacts">
          <Contact icon={email} value="Email" link="mailto:crf85@cornell.edu" />
          <Contact
            icon={linkedin}
            value="LinkedIn"
            link="www.linkedin.com/in/cesarferreyramansilla"
          />
          <Contact
            icon={github}
            value="Github"
            link="https://github.com/crfmc"
          />
          <Contact icon={phone} value="Phone" link="tel:123-456-7890" />
        </div>
        <p
          style={{
            backgroundColor: "black",
            padding: "2%",
            color: "white",
            bottom: "0",
            marginBottom: "0",
          }}
        >
          Copyright Â© All Rights Reserved by Cesar Ferreyra-Mansilla
        </p>
      </div>
    );
  }
}
